css:
@media screen {
    .has-variables{
        display: none;
    }
}
@media print {
    .has-variables{
        display: block; }
}
@page {
    @bottom-center {
        content: "Page " counter(page);
        font-size: 12px;
        margin-top:120px
        margin-bottom:120px
    }
}

table.table, table.table-bordered, th, td {
  border: 1px solid #ccc !important;
  border-collapse: collapse;
  padding: 6px 8px;
}
th {
    background-color: #f9f9f9;
}
td, th {
    padding: 6px 8px;
}
